import React,{useState} from 'react';
import {Link} from 'react-router-dom'
import './Style.css';

export default function Menu() {
  return (
    <div id='menu'>
        <Link to='/'><button className='navBtn'>Home Page</button></Link>
        <Link to='/tasks'><button className='navBtn'>All Tasks</button></Link>
        <Link to='/add-task'><button className='navBtn'>Add New Task</button></Link>
    </div>
  )
}
