    <div className='addDiv'>
        <input className='addInpt' onChange={(element)=>{setNewTask(element.target.value)}}type="text" id="newTask" placeholder="My new task"></input>
        <p className='dueTimeP'>Due date & Time:</p>
          <div>
            <DateTimePicker onChange={setTime} value={time} />
