import React,{useState} from 'react';
import './Style.css';

export default function Task(props) {
  const[taskStyle,setTaskStyle] = useState('taskStyle1');
  const[check,setCheck] = useState(true);

  const checking=()=>{
    setCheck(!check);
    // console.log(check);
    if(check===true){
      setTaskStyle('taskStyle2');
    }
    else{
      setTaskStyle('taskStyle1');
    }
  }

  return (
    <div className='taskDiv'>
        <p className={taskStyle}>Task name:{props.taskName}</p> 
        <input type="checkbox" onClick={checking}/>
        <button onClick={()=>{props.removeTask(props.index)}}>Remove</button>
    </div>
  )
}
