import React from 'react'
import Task from './Task'

export default function AllTasks(props) {
  return (
    <div>
        <ul>
            {props.tasks.map((val,i)=>(
                <Task key={i} />
                )  
            )}
        </ul>
    </div>
  )
}
{props.arr.map((val,i)=>{
  return<Room key={i} showRoom={props.showRoom} index={i} roomName={val.roomName} roomType={val.roomType} roomColor={val.roomColor}/>
}
)}
