import React,{useState} from 'react';
import './Style.css';
import DateTimePicker from 'react-datetime-picker';

export default function AddTask(props) {
    const[task,setNewTask] = useState('');
    const[value, onChange] = useState(new Date());
    const[taskTime,setTaskTime] = value.toTimeString();

    const validTask=()=>{
      if(task.length>0){
        props.add(task)
        return true
        console.log(value);
      }
      else{
        alert('Task too short!')
        return false
      }
    }

  return (
    <div className='addDiv'>
        <input className='addInpt' onChange={(element)=>{setNewTask(element.target.value)}}type="text" id="newTask" placeholder="My new task"></input>
        <p className='dueTimeP'>Due date & Time:</p>
          <div>
            <DateTimePicker onChange={onChange} value={value} />
          </div>
          {/* <input className='taskDate' onChange={(element)=>{setTaskDate(element.target.value)}}type="date" id="taskDate"></input>
          <input className='taskTime' onChange={(element)=>{setTaskTime(element.target.value)}}type="time" id="taskTime" ></input> */}
        <button className='addBtn' onClick={validTask}>Add Task</button>
    </div>
  )
}
