import React,{useState} from 'react';

export default function AddTask(props) {
    const [task,setNewTask] = useState('');

    

  return (
    <div>
        <input onChange={(element)=>{setNewTask(element.target.value)}}type="text" id="newTask" placeholder="My new task"></input>
        <button onClick={()=>props.add(task)}>Add Task</button>

    </div>
  )
}
