import React,{useState} from 'react';
import './Style.css';

export default function Task(props) {
  const[checkPress,setCheckPress] = useState(false);

  const check=()=>{
    setCheckPress(true);
    console.log(checkPress);
    if(checkPress == true){
      document.getElementsByClassName("task").style.color = "red";
      alert('check check')
    }
    else{
      document.getElementsByClassName("task").style.color = "blue";
    }
  }
  const removeTask=()=>{
    alert('Task has Removed');
  }

  return (
    <div className='taskDiv'>
        <p className="task">Task name:{props.taskName}</p> 
        <input type="checkbox" onClick={check}/>
        <button onClick={removeTask}>Remove</button>
    </div>
  )
}
