import React,{useState} from 'react';

export default function AddTask(props) {
    const [task,setNewTask] = useState('');

    const addNewTask=()=>{
        if(task.length < 1){
            alert ('ERROR');
          }
        else{
            console.log('task added');
            {props.add(task)};
            return true;
        }
    }

  return (
    <div>
        <input onChange={(element)=>{setNewTask(element.target.value)}}type="text" id="newTask" placeholder="My new task"></input>
        <button onClick={addNewTask(task)}>Add Task</button>

    </div>
  )
}
