import React,{useState} from 'react';
import { Link } from 'react-router-dom';
import './Style.css';
import DateTimePicker from 'react-datetime-picker';

export default function AddTask(props) {
  const[task,setNewTask] = useState('');
  const[time,setTime] = useState(new Date());
  const timeLeft = Math.abs ((new Date()) - time) ;

  let days = timeLeft / (24*60*60*1000);
  let hours = (days % 1) * 24;
  let minutes = (hours % 1) * 60;
    [days, hours, minutes] = [Math.floor(days), Math.floor(hours), Math.floor(minutes)];

  const timeToEnd = {}


    const validTask=()=>{
      if(task.length>0){
        props.add(task,time,days, hours, minutes);
        console.log(time);
        // console.log(days+'d', hours+'h', minutes+'m');
        return true
      }
      else{
        alert('Task too short!')
        return false
      }
    }

  return (
    <div className='addDiv'>
        <input className='addInpt' onChange={(element)=>{setNewTask(element.target.value)}}type="text" id="newTask" placeholder="My new task"></input>
        <p className='dueTimeP'>Due date & Time:</p>
          <div>
            <DateTimePicker onChange={setTime} value={time} />
          </div>

        <Link to='/tasks'><button className='addBtn' onClick={validTask}>Add Task</button></Link>
    </div>
  )
}
